    /*global chrome*/

import { useState } from "react";
import "./App.css"
import Schema from "./Schema"
import ReactDOM from "react-dom";
import React from "react";
function App() {


  const [clickStream, setClickStream] = useState([]);


  chrome.runtime.onMessage.addListener((request, sender,sendResponse)=>{

    console.log(request)
    setClickStream([...clickStream, request.elementDetected])
    sendResponse("I have received your message");
  })
var SiteTabId;
  const sendMessageToContentScript = (ToDO) => {


          console.log("Sending Message to Content Script")

          chrome.tabs.query({active: true}, function(tabs){
            SiteTabId = tabs[0].id
            chrome.tabs.sendMessage(SiteTabId, {Recording: ToDO}, function(response){

              console.log(response)
            });



        });





  }

  return (

<>


  <div>
  <button onClick = {() => sendMessageToContentScript('Start')}>Start</button>
  <br></br>
  <button onClick = {() => sendMessageToContentScript('Pause')}>Pause</button>
  <br></br>
  <button onClick = {() => sendMessageToContentScript('Stop')}>Stop</button>

  <ul>
                {clickStream.map(function(name, index){
                    return <li key={ index }>{name} <br></br></li>;
                  })}
  </ul>

  </div>
</>

      );
}

export default App;
