    /*global chrome*/

import { useState } from "react";
import "./App.css"
import Schema from "./Schema"
import ReactDOM from "react-dom";
import React from "react";
function App() {

  var contentScriptAddress;
  chrome.runtime.onMessage.addListener((request, sender,sendResponse)=>{
    contentScriptAddress = request;
    sendResponse("I have received your message");
  })

  const sendMessageToContentScript = (ToDO) => {

          console.log(ToDO)

          chrome.tabs.sendMessage(contentScriptAddress, {Recording: ToDO}, function(response){

            console.log(response)
          });




  }

  return (

<>


  <div>
  <button onClick = {() => sendMessageToContentScript('Start')}>Start</button>
  <br></br>
  <button onClick = {() => sendMessageToContentScript('Pause')}>Pause</button>
  <br></br>
  <button onClick = {() => sendMessageToContentScript('Stop')}>Stop</button>
  </div>
</>

      );
}

export default App;
