    /*global chrome*/

import { useState, Text } from "react";
import "./App.css"
import ReactDOM from "react-dom";
import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';



const useStyles = makeStyles((theme) => ({
  button: {
    display: 'block',
    marginTop: theme.spacing(1),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

const ControlledOpenSelect = ({Dictionary,SelectedUIControl, Setter}) => {
  const classes = useStyles();
  const [age, setAge] = React.useState('');
  const [open, setOpen] = React.useState(false);

  const handleChange = (event) => {

    Setter(prevState => ({
          ...prevState,
          [SelectedUIControl]:event.target.value 
      }));


    setAge(event.target.value);


  };

  const handleClose = () => {
    setOpen(false);

  };

  const handleOpen = () => {
    setOpen(true);
  };

  return (
    <div>
      <FormControl className={classes.formControl}>
        <Select
          labelId="demo-controlled-open-select-label"
          id="demo-controlled-open-select"
          open={open}
          onClose={handleClose}
          onOpen={handleOpen}
          value={Dictionary[SelectedUIControl]? Dictionary[SelectedUIControl] : age}
          onChange={handleChange}
        >

          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={"First Name"}>First Name</MenuItem>
          <MenuItem value={"Last Name"}>Last Name</MenuItem>
          <MenuItem value={"Age"}>Age</MenuItem>
          <MenuItem value={"Gender"}>Gender</MenuItem>
          <MenuItem value={"Profession"}>Profession</MenuItem>
          <MenuItem value={"Contact"}>Contact</MenuItem>
          <MenuItem value={"Address"}>Address</MenuItem>
        </Select>
      </FormControl>
    </div>
  );
};

function App() {


  const [clickStream, setClickStream] = useState([
    "ergergerger,ergergreger,regergregregerg,ergregergrgreggr,rgergrergergre",
    "we2wefrg3rfe,wef 3ecqwercrc,cercwqerc4rec,cqwrqcrcqwr,cqwrqewrccrwe",
    "cwercwercwecr,qcwrwcreqcwreqcwre,cqwerwqrqcwref,cqwrcwerqcerqcrrwqe,cqwecrec",
    "cwercewwww,cwwercwecrwecr,cwrqwercq3crccq3werc,cqwrcewcrwercweccewew"



  ]);



  const [pauseResume, setPauseResume] = useState("Pause");
  const [showSaveButton, setShowSaveButton] = useState(false);
  const [SequenceAndMapping, setSequenceAndMapping] = useState({});



  // chrome.runtime.onMessage.addListener((request, sender,sendResponse)=>{

  //   console.log(request)
  //   setClickStream([...clickStream, request.elementDetected])
  //   sendResponse("I have received your message");
  // }) 
  var SiteTabId;  
  const sendMessageToContentScript = (ToDO) => {
          if (ToDO=="Pause"){
            setPauseResume("Resume")
          }
          else if (ToDO == "Resume") {
            setPauseResume("Pause")
          }
          else if (ToDO == "Stop"){
            setShowSaveButton(true)
          }

          // chrome.tabs.query({active: true}, function(tabs){
          //   SiteTabId = tabs[0].id
          //   chrome.tabs.sendMessage(SiteTabId, {Recording: ToDO}, function(response){

          //     console.log(response)
          //   });



        // });





  }

  const SaveTheSequenceAndMapping = () => {
    console.log(SequenceAndMapping)
  }

  function replaceCommaLine(data) {
    //convert string to array and remove whitespace
    // let dataToArray = data.split(',').map(item => item.trim());
    //convert array to string replacing comma with new line
    return <div>
    {data.split(",").forEach(element => {
      element
    })
    }
    </div>
}

  return (

<>


  <div>
  <button id = "Start" onClick = {() => sendMessageToContentScript('Start')}>Start</button>
  <br></br>
  <button id = "Pause"  onClick = {() => sendMessageToContentScript(pauseResume)}>{pauseResume}</button>
  <br></br>
  <button id = "Resume" onClick = {() => sendMessageToContentScript('Stop')}>Stop</button>
  <br></br>
  <br></br>

  <ul>
                {clickStream.map(function(name, index){
                    return <div key={ index }>{
                      replaceCommaLine(name)

                    }   
                    <br></br>

                    <ControlledOpenSelect Dictionary = {SequenceAndMapping} SelectedUIControl = {name} Setter = {setSequenceAndMapping}/>
                    <hr></hr>
                    <hr></hr>
                    <br></br>

                    </div>;
                  })}
  </ul>


{
showSaveButton?
  <button id = "Save" onClick={SaveTheSequenceAndMapping}>Save</button>  
:
<div></div>
}

  </div>
</>

      );
}

export default App;
