import { useState } from "react";
import "./App.css"
import Schema from "./Schema"
function App() {


  const[schema, setSchema] = useState(true)
  const AddSchema = () => {
    setSchema(true)

  } 
  const RemoveSchema = () => {
    setSchema(false)

  } 
  return (

    <>

  <div className="overlay">
    <div className="dialog">
      <div className="dialog__content">
        <h2 className="dialog__title">Sequence Creation</h2>
        <p className="dialog__description">
          Choose to add Mapping or a Transition?
        </p>
      <hr />
      </div>
        {

           schema?

      <div className="dialog__content2">
        <Schema />
      <hr />
      </div>
            :

            <div></div>



        }




      <div className="dialog__footer">
        <button className="dialog__cancel" onClick = {RemoveSchema}>Transition</button>
        <button className="dialog__confirm" onClick = {AddSchema}>Mapping</button>
      </div>
    </div>
  </div>
</>

      );
}

export default App;
